package by.creepid.capicom.wrapper.cert;

import com.sun.org.apache.xerces.internal.impl.dv.util.Base64;
import java.security.cert.X509Certificate;
import junit.framework.TestCase;

/**
 *
 * @author rusakovich
 */
public class CertUtilTest extends TestCase {

    private static final String certContent = "";

    public CertUtilTest(String testName) {
        super(testName);
    }

    @Override
    protected void setUp() throws Exception {
        super.setUp();
    }

    @Override
    protected void tearDown() throws Exception {
        super.tearDown();
    }

    /**
     * Test of getX509Certificate method, of class CertUtil.
     */
    public void testGetX509Certificate() {
        System.out.println("**** getX509Certificate ******");
        X509Certificate result = CertUtil.getX509Certificate(Base64.decode(certContent));
        assertNotNull(result);
    }

}
